import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [ftrtData, setFtrtData] = useState(null);
  const [loading, setLoading] = useState(true);

  // Datos de ejemplo del sistema FTRT
  const planetaryData = {
    mercury: { name: 'Mercurio', contribution: 15.8, constellation: 'Tauro' },
    venus: { name: 'Venus', contribution: 12.4, constellation: 'Aries' },
    earth: { name: 'Tierra', contribution: 9.2, constellation: 'Tauro' },
    mars: { name: 'Marte', contribution: 3.1, constellation: 'Piscis' },
    jupiter: { name: 'J√∫piter', contribution: 28.7, constellation: 'Tauro' },
    saturn: { name: 'Saturno', contribution: 18.3, constellation: 'Piscis' },
    uranus: { name: 'Urano', contribution: 8.5, constellation: 'Tauro' },
    neptune: { name: 'Neptuno', contribution: 4.2, constellation: 'Piscis' }
  };

  useEffect(() => {
    // Simular carga de datos
    setTimeout(() => {
      setFtrtData({
        currentFTRT: 2.943,
        alertLevel: 'CRITICO',
        alertColor: 'üî¥',
        date: '2024-05-10'
      });
      setLoading(false);
    }, 1000);
  }, []);

  if (loading) {
    return (
      <div className="App">
        <header className="App-header">
          <h1>üåå CARGANDO SISTEMA FTRT...</h1>
        </header>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>üåå SISTEMA FTRT INTERACTIVO</h1>
        <p>Visualizaci√≥n de Fuerzas de Marea Planetarias</p>

        <div className="ftrt-container">
          <h2>üö® ESTADO ACTUAL DEL SISTEMA</h2>
          <div style={{fontSize: '24px', margin: '20px 0'}}>
            <strong>FTRT: {ftrtData.currentFTRT}</strong>
            <br />
            <span style={{color: '#ff4444'}}>Alerta: {ftrtData.alertLevel} {ftrtData.alertColor}</span>
            <br />
            <small>Fecha: {ftrtData.date}</small>
          </div>

          <h3>ü™ê CONTRIBUCIONES PLANETARIAS</h3>
          <div className="planet-grid">
            {Object.entries(planetaryData).map(([key, planet]) => (
              <div key={key} className="planet-card">
                <strong>{planet.name}</strong>
                <br />
                {planet.contribution}%
                <br />
                <small>{planet.constellation}</small>
              </div>
            ))}
          </div>

          <div style={{marginTop: '30px', padding: '20px', background: 'rgba(0,0,0,0.3)', borderRadius: '10px'}}>
            <h3>üìä EVENTOS HIST√ìRICOS</h3>
            <ul style={{textAlign: 'left', listStyle: 'none', padding: 0}}>
              <li>‚Ä¢ Carrington 1859: FTRT 3.21 üåü</li>
              <li>‚Ä¢ Halloween 2003: FTRT 4.87 üíú</li>
              <li>‚Ä¢ Mayo 2024: FTRT 2.94 üî¥</li>
            </ul>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
